/* Initial beliefs and rules */

/* Initial goals */

// Jon está sempre perguntando a Sam sobre o inverno
+!start
<- .send(sam, achieve, check_winter);
	.wait(3000);
	!start.

// Quando recebe a resposta de Sam, exibe no console
+winter(Status)
<- .print("Jon Snow: Sam disse - ", Status).

/* Initial beliefs and rules */

/* Initial goals */

// Inicialmente, Sam acha que o inverno ainda não chegou
+winter_is_not_here.

// Quando Jon pergunta, Sam responde baseado na belief
+!check_winter : winter_is_not_here
<- .send(jon, tell, winter("Ainda há tempo!"));
	.print("Sam: Não se preocupe, Jon. Ainda há tempo!").

+!check_winter : not winter_is_not_here
<- .send(jon, tell, winter("O inverno chegou!"));
	.print("Sam: Jon, é real... O inverno chegou!").

// Quando Bran avisa, Sam muda sua belief
+winter_vision
<- -winter_is_not_here;  // Remove a antiga belief
	.print("Sam: Bran me enviou uma visão... O inverno chegou mesmo!").

/* Initial beliefs and rules */

/* Initial goals */

// Bran espera um tempo e depois avisa Samwell
+!start_vision
<- .wait(10000);
	.send(sam, tell, winter_vision);
	.print("Bran: Eu vi no futuro... O inverno chegou!").
